INSERT INTO `tbmidia` (`id`, `titulo`, `descricao`, `genero`, `tipo`, `data_lancamento`, `poster`, `imgfundo`, `trailer`, `tamanho`, `id_tmdb`) VALUES
(6055, 'Great British Menu', '', '[\'Reality\']', 'tv', '2006-04-10', 'https://image.tmdb.org/t/p/w500/4fEs08TXvJ85E0d9SxGm69D3hsw.jpg', 'https://image.tmdb.org/t/p/original/fW5dqCVKbWblw8iVehDCz2uFiu1.jpg', NULL, 15, 3431),
(6056, '미스 마: 복수의 여신', 'Ms. Ma é uma mulher injustamente acusada do assassinato de sua própria filha. Determinada a provar sua inocência, ela escapa da prisão e se refugia em uma pequena vila, onde acaba se envolvendo nos mistérios locais enquanto busca pistas sobre o verdadeiro culpado. Com uma mente afiada e um espírito inabalável, Ms. Ma enfrenta perigos, conspirações e inimigos implacáveis, desvendando segredos chocantes sobre seu passado. Inspirada na icônica \"Miss Marple\" de Agatha Christie, a série combina suspense psicológico e reviravoltas inesperadas, mantendo o público intrigado até o desfecho surpreendente.', '[\'Mistério\', \'Drama\']', 'tv', '2018-10-06', 'https://image.tmdb.org/t/p/w500/4FnMzT8x3fwXEGlD0XcYPtvkTC8.jpg', 'https://image.tmdb.org/t/p/original/nDjyhbPTWzXKyB4gYFonwQeMkyq.jpg', NULL, 1, 82658),
(6057, 'A Melhor Escolha de Todos os Tempos', 'Mai Chenghuan, uma garota pós-95 nascida em uma família comum em Xangai, propôs casamento a seu namorado, Xin Jialiang, a pedido de sua mãe, Liu Wanyu. No entanto, as condições econômicas favoráveis ​​da família Xin tornaram esse relacionamento emocional originalmente igualitário desequilibrado.', '[\'Drama\', \'Família\']', 'tv', '2024-04-09', 'https://image.tmdb.org/t/p/w500/oT0TPzU5udnZF9zi6Ds4w5pKT3P.jpg', 'https://image.tmdb.org/t/p/original/4mi2FoPWCvwkuF9go5qNIif890M.jpg', NULL, 1, 232948),
(6058, 'Αν Ήμουν Πλούσιος', '', '[\'Comédia\']', 'tv', '2019-09-16', 'https://image.tmdb.org/t/p/w500/5rdDPx9mwJIcQW6eR9ATgiXQZOX.jpg', 'https://image.tmdb.org/t/p/original/pL5X7rNzlpW8MpvBggpC94FlOZz.jpg', NULL, 1, 99379),
(6059, 'Destilando amor', 'Na região onde a famosa bebida mexicana Tequila é produzida, começa uma história de paixão, discórdias e desencontros. A novela mostra o duro caminho que uma pessoa tem que fazer quando realmente está em busca de algo extraordinário. O amor, a saudade, as incertezas e a superação de uma vida de obstáculos, que vão envolver o telespectador. Maria Teresa García, conhecida como Gaivota, trabalha em campos agaveros para produzir tequila. Vive com sua mãe, Clarita, que é aficionada por telenovelas. Os campos agaveros são propriedade da família Montalvo. Logo após a morte do dono da propriedade, no velório ela conhece um dos netos do proprietário (Amador M), Rodrigo Montalvo, Gaivota se apaixona por ele, que por sua vez lhe corresponde.', '[\'Drama\', \'Soap\']', 'tv', '2007-01-22', 'https://image.tmdb.org/t/p/w500/L3MPoQSvfbHZAJEdskXTM1vdHi.jpg', 'https://image.tmdb.org/t/p/original/kez8Vt5lIus3vrcN6NTmUezKee5.jpg', NULL, 1, 12568),
(6060, 'Ein starkes Team', '', '[\'Drama\', \'Crime\']', 'tv', '1994-03-28', 'https://image.tmdb.org/t/p/w500/wrLDjgOHk0MLQNSdAs1vzUOMuQc.jpg', 'https://image.tmdb.org/t/p/original/4AChEz1ohSgUHuLyhlw7oKTXURe.jpg', NULL, 1, 41116),
(6061, 'Marine Boy', '', '[\'Família\', \'Animação\', \'Action & Adventure\']', 'tv', '1966-10-06', 'https://image.tmdb.org/t/p/w500/dsPs0KF4cOcD32bBERP4qhzVVGO.jpg', 'https://image.tmdb.org/t/p/original/icrHnXgTqC3EY4Pg68tWXg28xpa.jpg', NULL, 1, 9283),
(6062, 'Ai Yori Aoshi', 'Kaoru Hanabishi, um estudante universitário que mora sozinho, conheci uma garota linda, mas desnorteado vestida com um quimono numa estação de trem. Ele se ofereceu para guiar seu caminho para o endereço que estava procurando, que passou a ser no seu bairro, mas acabou por ser um terreno baldio. Não sabendo o que fazer a seguir, Kaoru convidou a garota arrasada ao seu apartamento e pediu para todos os indícios adicionais para o seu destino. Ela lhe forneceu uma foto de duas crianças a quem Kauru imediatamente identificada como a si mesmo e Sakuraba Aoi, seu amigo de infância. Acontece que a garota na frente dele é Aoi Sakuraba si mesma, sua noiva prometida, que veio todo o caminho para Tóquio para se casar com ele. Sua revelação não era apenas surpreendente, mas também lembrou a parte mais profunda da memória Kaoru por que ele deixou a família Hanabishi em primeiro lugar.', '[\'Animação\', \'Comédia\', \'Drama\']', 'tv', '2002-04-10', 'https://image.tmdb.org/t/p/w500/icMMC28fBvXs2EsXSdrqnDnIxeR.jpg', 'https://image.tmdb.org/t/p/original/z4GtNULtH7UCjrzg6PoCCjpzDcs.jpg', NULL, 2, 42567),
